function ass4 ()  #a = imread("Image_1.jpeg");  #b = imread("Image_2.jpeg");  #imshow(a);  #[first_x; first_y] = ginput(8);   #imshow(b);  #[second_x; second_y] = ginput(8);     #EXAMPLE POINTS because ginput not working  first_x = [2, 1, 1, 0];  first_y = [4, 0, 1, 0];  first = [first_x; first_y]  second_x = [0, 639, 639, 0];  second_y = [0, 0, 639, 639];  second = [second_x; second_y]      # -----------normalizing image points----------------  first_norm = normalize_image_points(first)  second_norm = normalize_image_points(second)    # --------------Formulating a homogeneous linear equation----------   # --------------SVD----------   # --------- Enforcing the internal constraint ----------------  endfunctionfunction norm_mat = normalize_image_points(x)# Calculate center of masscentroid = mean(x,2); # Calculate distances of each point to the centroiddist = sqrt(sum((x - centroid) .^ 2));# Calculate current mean distancemean_dist = mean(dist);#shift the image coordinate systems to the respective centroids #point coodinates in new cos: (x – centoid-x, y – centroid-y)x = x-centroid;# scale so that the mean distance from the origin to a point equals sqrt(2)#multiplying old coordinates by sqrt(2) and dividing out the mean distance norm_mat = x * sqrt(2) / mean_dist;end